def get_answer
	s = INPUT
	left = s.tr_s(")", "").chars.count
	right = s.tr_s("(", "").chars.count
	answer = left - right
	return answer
end

def get_position
	floor = 0
	x = INPUT.chars.map { |char|
		if char == "("
			+1
		elsif char == ")"
			-1
		else
			raise "invalid char #{char}"
		end
	}.reduce([]) { |rlt, move|
		rlt + [(rlt.last || 0) + move]
	}.each.with_index.reduce(nil) { |r, floor_index|
		floor, index = floor_index
		if floor < 0 
			r ||= index
		end
		r
	}
	puts x + 1

end

puts get_position