def get_answer
	s = INPUT
	left = s.tr_s(")", "").chars.count
	right = s.tr_s("(", "").chars.count
	answer = left - right
	return answer
end

def get_position
	floor = 0
	INPUT.chars.each.with_index do |char, idx|
		if char == "("
			floor += 1
		else
			floor -= 1
		end
		if floor < 0
			return idx + 1
		end
	end
end
